+// 获取当前登录用户信息（含角色、菜单、按钮权限）
+exports.me = async (req, res, next) => {
+  try {
+    const userId = req.user.id;
+    const { User, Role, Permission, Resource, ResourceAction } = require("../models");
+
+    // 查询用户及角色
+    const user = await User.findByPk(userId, {
+      include: [
+        {
+          model: Role,
+          as: "roles",
+          include: [
+            {
+              model: Permission,
+              as: "Permissions",
+              through: { attributes: [] },
+            },
+            {
+              model: Resource,
+              as: "Resources",
+              through: { attributes: [] },
+            },
+          ],
+          through: { attributes: [] },
+        },
+      ],
+      attributes: { exclude: ["password"] },
+    });
+    if (!user) {
+      return res.status(404).json(ResponseUtil.error("User not found", 404));
+    }
+
+    // 角色清单
+    const roles = user.roles.map((role) => role.code);
+
+    // 菜单资源（合并所有角色的资源，去重）
+    const resourceMap = {};
+    user.roles.forEach((role) => {
+      (role.Resources || []).forEach((r) => {
+        resourceMap[r.id] = r;
+      });
+    });
+    const routes = Object.values(resourceMap);
+
+    // 按钮权限（合并所有角色的权限，去重，提取 action/code）
+    const permSet = new Set();
+    user.roles.forEach((role) => {
+      (role.Permissions || []).forEach((p) => {
+        permSet.add(p.name);
+      });
+    });
+    // 也可根据 ResourceAction 进一步细化按钮权限
+    // const actions = await ResourceAction.findAll();
+    // ...
+    const permissions = Array.from(permSet);
+
+    res.json(
+      ResponseUtil.success({
+        user: {
+          id: user.id,
+          username: user.username,
+          email: user.email,
+          status: user.status,
+        },
+        roles,
+        routes,
+        permissions,
+      })
+    );
+  } catch (err) {
+    next(err);
+  }
+};

